from machine import Pin, Signalimport pins_cfgimport timeimport _threadled_signal = Signal(Pin(pins_cfg.ledPin, Pin.OUT, value=1), invert=True)time_on = 0time_off = 1def led_thread():    global time_on, time_off    while True:        if time_on > 0:            led_signal.on()            time.sleep(time_on)        if time_off > 0:            led_signal.off()            time.sleep(time_off)        def init():    _thread.start_new_thread(led_thread, ())def led_fast():    global time_on, time_off    time_on = 0.1    time_off = 0.1def led_slow():    global time_on, time_off        time_on = 0.5    time_off = 0.5def led_on():    global time_on, time_off    time_on = 0.3    time_off = 0def led_off():    global time_on, time_off    time_on = 0    time_off = 0.3